						["Health Update"] = "function (self, unitId, unitFrame, envTable)\n    \n    if (not envTable.CurrentHealth) then\n        envTable.CurrentHealth = unitFrame.healthBar.currentHealth\n        return\n    end\n    \n    if (not envTable.CanAnimate) then\n        return \n    end\n    \n    local healthBar = unitFrame.healthBar\n    \n    if (healthBar.currentHealth < envTable.CurrentHealth) then\n        --see if the health lost is bigger then the threshold\n        local threshold = healthBar.currentHealthMax * envTable.MinHealthPercent\n        local healthDifference = envTable.CurrentHealth - healthBar.currentHealth\n        \n        if (healthDifference > threshold) then\n            --call the animation function\n            envTable.PlayHealthAnimation (healthBar, healthDifference)\n        end\n    end\n    \n    envTable.CurrentHealth = healthBar.currentHealth\n    \nend",
						["Nameplate Added"] = "function (self, unitId, unitFrame, envTable)\n    \n    envTable.CanAnimate = true\n    \n    envTable.CreateTextures (unitFrame)\n    \n    --store the current health\n    envTable.CurrentHealth = unitFrame.healthBar.currentHealth\n    \n    --reset which texture to use on the next animation\n    envTable.CurrentTexture = 1\n    \nend",
