						["Constructor"] = "function (self, unitId, unitFrame, envTable)\n    \n    envTable.MinHealthPercent = 0.03 -- require 3% of health loss to trigger an animation\n    \n    --animation settings (require a /reload)\n    envTable.AnimationDuration = 0.55\n    envTable.AnimationTranslationX = 6\n    envTable.AnimationTranslationY = 1\n    \n    --internal stuff\n    envTable.CurrentHealth = false\n    envTable.AmountTextures = 10\n    --create the textures used by the animation\n    --it creates several textures to loop in case of a health loss when a texture is still playing its animation\n    function envTable.CreateTextures (unitFrame)\n        \n        local healthBar = unitFrame.healthBar\n        \n        if (healthBar.loseHealthTextures) then\n            return\n        end\n        \n        healthBar.loseHealthTextures = {}\n        \n        for i = 1, envTable.AmountTextures do\n            local loseHealthTexture = healthBar:CreateTexture (nil, \"overlay\")\n            tinsert (healthBar.loseHealthTextures, loseHealthTexture)\n            \n            loseHealthTexture.Animation = Plater:CreateAnimationHub (loseHealthTexture, \n                function()\n                    loseHealthTexture:Show()\n                end, \n                function()\n                    loseHealthTexture:Hide()\n            end)\n            \n            local anim = loseHealthTexture.Animation\n            Plater:CreateAnimation (anim, \"translation\", 1, envTable.AnimationDuration, envTable.AnimationTranslationX, envTable.AnimationTranslationY)\n            Plater:CreateAnimation (anim, \"alpha\", 1, envTable.AnimationDuration, 1, 0)\n        end\n        \n        envTable.CurrentTexture = 1\n    end\n    \n    --play animation function\n    function envTable.PlayHealthAnimation (healthBar, healthLoss)\n        local width = healthBar:GetWidth()\n        local _, maxValue = healthBar:GetMinMaxValues()\n        \n        --how much health each pixel value\n        local pixelPerHealth = width / maxValue\n        \n        --what is the size of the lose health texture\n        local loseTextureWidth = healthLoss * pixelPerHealth\n        \n        --prevent the size to be bigger than the nameplate size, in cases of damaging shields\n        local maxSize = (maxValue - healthBar.currentHealth) * pixelPerHealth\n        loseTextureWidth = min (loseTextureWidth, maxSize)\n        \n        --get a texture to use\n        local loseHealthTexture = healthBar.loseHealthTextures [envTable.CurrentTexture]\n        if (not loseHealthTexture) then\n            return\n        end\n        loseHealthTexture:Show()\n        \n        --loop textures\n        envTable.CurrentTexture = (envTable.CurrentTexture % envTable.AmountTextures) + 1\n        \n        loseHealthTexture:SetWidth (max (loseTextureWidth, 1))\n        loseHealthTexture:SetHeight (healthBar:GetHeight())\n        loseHealthTexture:SetAlpha (1)\n        loseHealthTexture:SetTexture (healthBar:GetTexture())\n        loseHealthTexture:SetVertexColor (healthBar.barTexture:GetVertexColor())\n        \n        loseHealthTexture:ClearAllPoints()\n        loseHealthTexture:SetPoint (\"left\", healthBar, \"left\", healthBar.currentHealth * pixelPerHealth, 0)\n        \n        if (loseHealthTexture.Animation:IsPlaying()) then\n            loseHealthTexture.Animation:Stop()\n        end\n        \n        loseHealthTexture.Animation:Play()\n    end\n    \n    --stop and hide all texture\n    function envTable.StopAllHealthAnimation (healthBar)\n        for i = 1, envTable.AmountTextures do\n            local loseHealthTexture = healthBar.loseHealthTextures [i]\n            \n            if (loseHealthTexture.Animation:IsPlaying()) then\n                loseHealthTexture.Animation:Stop()\n            end            \n            \n            loseHealthTexture:Hide()\n        end\n    end\n    \n    \nend",
