						["Constructor"] = "function (self, unitId, unitFrame, envTable, modTable)\n    function  updateLastName(self, unitId, unitFrame, envTable) \n        local name = unitFrame.healthBar.unitName:GetText()\n        \n        if (name) then            \n            local hasSpace = string.find(name, \" \")\n            local name = \"\"\n            local  skippedNames  = { [144747] = true, -- pakus-aspect\n                [144767] = true, -- gonks-aspect\n                [144963] = true, -- kimbuls-aspect \n                [144941] = true, -- akundas-aspect    \n                [148894] = true, -- lost-soul\n                [148716] = true, -- risen-soul\n                [148893] = true, -- tormented-soul\n                [135989] = true, -- shieldbearer-of-zul\n                [127879] = true, -- shieldbearer-of-zul\n                [131527] = true, -- lord-waycrest\n                [131545] = true, -- lady-waycrest\n                [131863] = true, -- raal\n                [144324] = true, -- gorak-tul\n                [127484] = true, -- jes-howlis\n                [130655] = true, -- bobby-howlis\n                [155432] = true, -- enchanted-emissary\n                [155433] = true, -- void-touched-emissary\n                [135764] = true, -- explosive-totem\n                [135761] = true, -- thundering-totem\n                [135765] = true, -- torrent-totem\n                [135759] = true} -- earthwall-totem\n            \n            if (hasSpace and not skippedNames [unitFrame.namePlateNpcId]) then\n                local realUnitName = UnitName(unitId)\n                for word in string.gmatch(realUnitName, \"%S+\") do \n                    name = word\n                end \n                unitFrame.healthBar.unitName:SetText (name)\n            end\n        end\n    end    \nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
